mapper.select:
select 

from user_gateway
where uid = #{uid,jdbcType=VARCHAR}
  and application_id = #{applicationId,jdbcType=VARCHAR}
  and application_gateway_id = #{applicationGatewayId,jdbcType=VARCHAR}

mapper.delete:
delete from user_gateway
where uid = #{uid,jdbcType=VARCHAR}
  and application_id = #{applicationId,jdbcType=VARCHAR}
  and application_gateway_id = #{applicationGatewayId,jdbcType=VARCHAR}

mapper.insert:
insert into user_gateway (uid, application_id, application_gateway_id, 
  application_place_id, create_user, create_date
  )
values (#{uid,jdbcType=VARCHAR}, #{applicationId,jdbcType=VARCHAR}, #{applicationGatewayId,jdbcType=VARCHAR}, 
  #{applicationPlaceId,jdbcType=VARCHAR}, #{createUser,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}
  )

mapper.update:
update user_gateway


    application_place_id = #{applicationPlaceId,jdbcType=VARCHAR},


    create_user = #{createUser,jdbcType=VARCHAR},


    create_date = #{createDate,jdbcType=TIMESTAMP},


where uid = #{uid,jdbcType=VARCHAR}
  and application_id = #{applicationId,jdbcType=VARCHAR}
  and application_gateway_id = #{applicationGatewayId,jdbcType=VARCHAR}

update user_gateway
set application_place_id = #{applicationPlaceId,jdbcType=VARCHAR},
  create_user = #{createUser,jdbcType=VARCHAR},
  create_date = #{createDate,jdbcType=TIMESTAMP}
where uid = #{uid,jdbcType=VARCHAR}
  and application_id = #{applicationId,jdbcType=VARCHAR}
  and application_gateway_id = #{applicationGatewayId,jdbcType=VARCHAR}
